import React from 'react';
import { StyleSheet, View, Text, SafeAreaView, TextInput, Button} from 'react-native';

export default function App() {

    const [text, onChangeText] = React.useState('');
    const [number, onChangeNumber] = React.useState('');

  return (
    <View style={styles.container}>
     <View style={styles.card}>
      <Text style={styles.titulo}> Nice Imposto</Text>
     </View>
     <View style={styles.cardForm}>
       <Text style={styles.subtitulo}>Preencha o formul√°rio abaixo para ver seu imposto de renda</Text>
     <SafeAreaView>
      <TextInput
        style={styles.input}
        onChangeText={onChangeText}
        value={text}
        placeholder="Digite seu Nome"

      />
      <TextInput
        style={styles.input}
        onChangeText={onChangeNumber}
        value={number}
        placeholder="Digite seu rendimento"
        keyboardType="numeric"

      />
      <TextInput
        style={styles.input}
        onChangeText={onChangeNumber}
        value={number}
        placeholder="Dgite seu CPF"
        keyboardType="numeric"
      />
    </SafeAreaView>
    <Button style={styles.botao}
        title="Enviar dados"
        color="#0B2447"
      />
    </View>
  </View>
   );
  };

const styles = StyleSheet.create({
  container: {
    flex:1,
    backgroundColor: '#0B2447',
    alignItems:"center"
  },

  titulo:{
    fontSize:80,
    alignSelf:"center",
    textAlign:"center",
    fontWeight:"bold"
  },
  input: {
    height: 50,
    margin: 12,
    borderWidth: 1,
    width: 300,
    borderRadius:"15px",
    marginLeft:5,
    marginBottom: 15
  },
  card: {
    width:"82%",
    height:"30%",
    backgroundColor:'#E9AA21', 
    borderRadius:'5px', 
    marginTop:'5%',

  },
  cardForm: { 
    alignItems:"center",
    width:"90%",
    height:"50%",
    backgroundColor:'#FFFFFF', 
    borderRadius:'5px', 
    marginTop:'15%',
    textAlign:"center",
  },
  subtitulo:{
    fontSize:20,
    marginLeft:5,
    marginTop:30,
    marginBottom:10
  },
});
